    + (daysold + '').padStart(3, '0') + "天" 
    + (hrsold + '').padStart(2, '0') + "小时" 
    + (minsold + '').padStart(2, '0')  + "分" 
    + (seconds + '').padStart(2, '0') + "秒"
    show.innerHTML = t;
    }, 1000)
}());
